export default "<html>\n\n<head>\n  <title>Psychopaint Drawing Board</title>\n  <meta name=\"description\" content=\"Psychopaint Drawing Board based on SignaturePad\">\n\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1, minimum-scale=1, maximum-scale=1, user-scalable=no\">\n  <link rel=\"preconnect\" href=\"https://fonts.gstatic.com\"> \n  <link href=\"https://fonts.googleapis.com/css2?family=Kanit:wght@100;300;400;500;600&display=swap\" rel=\"stylesheet\">\n  <meta name=\"apple-mobile-web-app-capable\" content=\"yes\">\n  <meta name=\"apple-mobile-web-app-status-bar-style\" content=\"black\">\n  <link rel=\"stylesheet\" href=\"https://fonts.googleapis.com/icon?family=Material+Icons\">\n  <link href=\"https://fonts.googleapis.com/css2?family=Pridi:wght@300;400&display=swap\" rel=\"stylesheet\">\n  <style>\n    *,\n    *::before,\n    *::after {\n      box-sizing: border-box;\n    }\n\n    html,\n    body {\n      width: 100%;\n      height: 100%;\n      margin: 0;\n      background-color: rgb(240, 240, 240);\n    }\n\n    /* Portrait */\n    @media screen and (orientation:portrait) {\n      /* Portrait styles */\n        #can {\n          display: block;\n          background-color: white;\n          position: absolute;\n          top: 0px;\n          left: 0px;\n          height: calc(100% - 122px);\n          width: 100%;\n        }\n    }\n\n    /* Landscape */\n    @media screen and (orientation:landscape) {\n      /* Landscape styles */\n      #can {\n        display: block;\n        background-color: white;\n        position: absolute;\n        top: 0px;\n        left: 0px;\n        height: calc(100% - 122px);\n        width: 100%;\n      }\n    }\n\n    .buttonClr {\n      -webkit-appearance: none !important;\n      background-color: #deddff;\n      border-color: #8480F5;\n      border: 1;\n      color: #706bf8;\n      padding: 10px;\n      text-align: center;\n      text-decoration: none;\n      display: inline-block;\n      font-size: 16px;\n      margin: 5px 2px;\n      cursor: pointer;\n      border-radius: 25px;\n      /* font-family: 'Pridi', serif; */\n      font-family: 'Kanit', sans-serif;\n    }\n\n    .button {\n      -webkit-appearance: none !important;\n      background-color: #8480F5;\n      /* Green */\n      border: none;\n      color: white;\n      padding: 10px;\n      text-align: center;\n      text-decoration: none;\n      display: inline-block;\n      font-size: 16px;\n      margin: 5px 2px;\n      cursor: pointer;\n      border-radius: 25px;\n      /* font-family: 'Pridi', serif; */\n      font-family: 'Kanit', sans-serif;\n    }\n\n    #pen {\n      position: absolute;\n      left: 12px;\n      bottom: 0;\n      width: 48px;\n    }\n\n    #log {\n      position: absolute;\n      left: 50%;\n      bottom: 6px;\n      transform: translate(-50%, -50%);\n      font-family: 'Kanit', sans-serif;\n    }\n\n    #erasor {\n      position: absolute;\n      left: 60px;\n      bottom: 0;\n      width: 98px;\n    }\n\n    #saveBtn {\n      position: absolute;\n      right: 12px;\n      bottom: 6px;\n    }\n\n    #clearBtn {\n      /* display: none; */\n      position: absolute;\n      right: 12px;\n      bottom: 54px;\n    }\n  </style>\n</head>\n<script type=\"text/javascript\">\n  const penActive =\n    \"data:image/png;base64,\"\n\n  const penInActive =\n    \"data:image/png;base64,\"\n\n  const eraserActive =\n    \"data:image/png;base64,\"\n\n  const eraserInActive =\n    \"data:image/png;base64,\"\n\n\n  var canvas, ctx, flag = false,\n    prevX = 0,\n    currX = 0,\n    prevY = 0,\n    currY = 0,\n    dot_flag = false;\n\n  var x = \"black\",\n    y = 2;\n  let mode = 'pen'\n\n  let wasCanvasTouched = false\n  let isCanvasEmpty = true\n\n  window.onresize = resizeCanvas;\n\n  // timer\n  let totalSeconds = 0;\n  let isCounting = false\n  setInterval(setTime, 1000);\n\n  function setTime() {\n    if(isCounting) {\n      ++totalSeconds;\n      document.getElementById(\"log\").innerHTML = \"เวลาที่ใช้ในการวาด \" + pad(parseInt(totalSeconds / 60) + \" นาที \" + totalSeconds % 60 + \" วินาที\");\n    }\n  }\n\n  function pad(val) {\n    var valString = val + \"\";\n    if (valString.length < 2) {\n      return \"0\" + valString;\n    } else {\n      return valString;\n    }\n  }\n  // end timer\n\n  document.addEventListener('message', (e) => {\n    document.getElementById('log').innerHTML = \"แตะปากกาลงบนหน้าจอเพื่อเริ่มต้นวาด\"\n    let msg = e.data.split('|')\n    if (msg[0] == \"clearDrawing\") {\n      erase(true)\n    }\n  })\n\n  window.addEventListener('message', (e) => {\n    document.getElementById('log').innerHTML = \"แตะปากกาลงบนหน้าจอเพื่อเริ่มต้นวาด\"\n    let msg = e.data.split('|')\n    if (msg[0] == \"clearDrawing\") {\n      erase(true)\n    }\n  })\n\n  function usePen() {\n    let penBtn = document.getElementById('pen')\n    let erasorBtn = document.getElementById('erasor')\n    penBtn.src = penActive\n    erasorBtn.src = eraserInActive\n    mode = 'pen'\n  }\n\n  function useErasor() {\n    let penBtn = document.getElementById('pen')\n    let erasorBtn = document.getElementById('erasor')\n    penBtn.src = penInActive\n    erasorBtn.src = eraserActive\n    mode = 'erasor'\n  }\n\n  function logger (msg) {\n    console.log(msg)\n    if(window.ReactNativeWebView) {\n      window.ReactNativeWebView.postMessage(msg)\n    }\n  }\n\n  function resizeCanvas() {\n    // fix blurry\n    canvas = document.getElementById('can');\n    ctx = canvas.getContext(\"2d\");\n    var ratio =  Math.max(window.devicePixelRatio || 1, 1);\n    canvas.width = canvas.offsetWidth * ratio;\n    canvas.height = canvas.offsetHeight * ratio;\n    canvas.getContext(\"2d\").scale(ratio, ratio);\n    // end fix blurry\n    // logCanvasSize()\n  }\n  function logCanvasSize() {\n    w = canvas.width;\n    h = canvas.height;\n    logger(\"_canvasSize|\" + w + \",\" + h);\n  }\n\n  function init() {\n    canvas = document.getElementById('can');\n    ctx = canvas.getContext(\"2d\");\n    ctx.canvas.width = window.innerWidth;\n    ctx.canvas.height = window.innerHeight - 122;\n\n    // fix blurry\n    resizeCanvas()\n    // end fix blurry\n\n    canvas.addEventListener(\"touchmove\", function(e) {\n      e.preventDefault();\n\n      if (e.touches[0].touchType !== undefined)\n        if (e.touches[0].touchType == 'direct')\n          return 0;\n\n      var touch = e.targetTouches[0];\n      findxy('move', touch)\n    }, false);\n    canvas.addEventListener(\"touchstart\", function(e) {\n      e.preventDefault();\n\n      // document.getElementById('btn').value = e.touches[0].touchType // --> direct / stylus / undefined\n      if (e.touches[0].touchType !== undefined)\n        if (e.touches[0].touchType == 'direct')\n          return 0;\n\n      if (e.targetTouches.length === 1) {\n        // timer\n        if (!isCounting) {\n          totalSeconds = 0\n          isCounting = true\n          isCanvasEmpty = false\n        }\n        // end timer\n        wasCanvasTouched = true\n        var touch = e.changedTouches[0];\n        findxy('down', touch)\n      }\n\n    }, false);\n    canvas.addEventListener(\"touchend\", function(e) {\n\n      // var wasCanvasTouched = e.target === canvas;\n      if (wasCanvasTouched) {\n        wasCanvasTouched = false\n        e.preventDefault();\n        var touch = e.changedTouches[0];\n        findxy('up', e)\n      }\n    }, false);\n  }\n\n  function draw() {\n\n    if (mode == \"pen\") {\n      ctx.globalCompositeOperation = 'source-over';\n      y = 2\n    } else {\n      ctx.globalCompositeOperation = 'destination-out';\n      y = 20\n    }\n\n    ctx.beginPath();\n    ctx.moveTo(prevX, prevY);\n    ctx.lineTo(currX, currY);\n    ctx.strokeStyle = x;\n    ctx.lineWidth = y;\n    ctx.stroke();\n    ctx.closePath();\n  }\n\n  function erase(dontTellReactNative) {\n    /*\n    var m = confirm(\"Want to clear\");\n    if (m) {\n      ctx.clearRect(0, 0, w, h);\n      logger(\"_clearCanvas|\")\n      //document.getElementById(\"canvasimg\").style.display = \"none\";\n    }\n    */\n    w = canvas.width;\n    h = canvas.height;\n    ctx.clearRect(0, 0, w, h);\n    if (dontTellReactNative !== null)\n      logger(\"_clearCanvas|\")\n    // timer\n    if (isCounting) {\n      isCounting = false\n      isCanvasEmpty = true\n      document.getElementById('log').innerHTML = \"แตะปากกาลงบนหน้าจอเพื่อเริ่มต้นวาด\"\n    }\n    // end timer\n  }\n\n  function save() {\n\n    /*\n    if (isCanvasEmpty)\n      return 0\n      */\n\n    logCanvasSize()\n    // add white bg\n    let destinationCanvas = document.createElement(\"canvas\");\n    destinationCanvas.width = canvas.width;\n    destinationCanvas.height = canvas.height;\n    let destCtx = destinationCanvas.getContext('2d');\n    destCtx.fillStyle = \"#FFFFFF\";\n    destCtx.fillRect(0,0,canvas.width,canvas.height);\n    destCtx.drawImage(canvas, 0, 0);\n    // end\n    var dataURL = destinationCanvas.toDataURL();\n    logger(\"_saveCanvas|\" + dataURL)\n    // timer\n    if (isCounting) {\n      isCounting = false\n    }\n    // end timer\n  }\n\n  function findxy(res, e) {\n    if (res == 'down') {\n      prevX = currX;\n      prevY = currY;\n      currX = e.clientX - canvas.offsetLeft;\n      currY = e.clientY - canvas.offsetTop;\n\n      var date = new Date();\n      var time = date.toLocaleTimeString('en-GB') + ':' + date.getMilliseconds();\n      logger(\"_strokeBegin|\" + mode + \"|\" + time)\n      logger(\"_strokeUpdate|\" + currX + \",\" + currY)\n\n      flag = true;\n      dot_flag = true;\n      if (dot_flag) {\n\n        if (mode == \"pen\") {\n          ctx.globalCompositeOperation = 'source-over';\n          y = 2\n        } else {\n          ctx.globalCompositeOperation = 'destination-out';\n          y = 20\n        }\n\n        ctx.beginPath();\n        ctx.fillStyle = x;\n        ctx.fillRect(currX, currY, 2, 2);\n        ctx.closePath();\n        dot_flag = false;\n      }\n    }\n    if (res == 'up') {\n      var date = new Date();\n      var time = date.toLocaleTimeString('en-GB') + ':' + date.getMilliseconds();\n      logger(\"_strokeEnd|\" + time)\n      flag = false;\n    }\n    if (res == 'move') {\n      if (flag) {\n        prevX = currX;\n        prevY = currY;\n        currX = e.clientX - canvas.offsetLeft;\n        currY = e.clientY - canvas.offsetTop;\n        logger(\"_strokeUpdate|\" + currX + \",\" + currY)\n        draw();\n      }\n    }\n  }\n</script>\n\n<body onload=\"init()\">\n  <canvas id=\"can\"></canvas>\n  <label id=\"log\">แตะปากกาลงบนหน้าจอเพื่อเริ่มต้นวาด</label>\n  <input type=\"button\" class=\"buttonClr\" value=\"กลับหน้าหลัก\" id=\"saveBtn\" size=\"30\" onclick=\"save()\">\n  <input type=\"button\" class=\"button\" value=\"ลบทั้งหมด\" id=\"clearBtn\" size=\"23\" onclick=\"erase()\">\n  <input type=\"image\" id='pen'\n    src=\"data:image/png;base64,\"\n    onclick=\"usePen()\" />\n  <input type=\"image\" id='erasor'\n    src=\"data:image/png;base64,\"\n    onclick=\"useErasor()\" />\n</body>\n\n</html>\n"